import React, { useEffect, useState } from 'react';
import * as d3 from "d3";
import "d3-selection-multi";

const Graph = ({fields, backgroundColor, textColor, width}) => {
    const [ txtColor, setTxtColor ] = useState('000000');
    const svgRef = React.createRef();
    // const recRef = React.createRef();
    const gRef = React.createRef();

    // const [ height, setHeight ] = useState(props.height);

    const updateData = (g) => {
        g.exit().remove()
    }



    useEffect(() => {
        setTxtColor(textColor)
        const dataDomain = Object.keys(fields).map( field => {
            return parseInt(fields[field]);
        })

        const xScale = d3.scaleLinear()
            .domain([ 0, d3.max(dataDomain)])
            .range([ 0, width])

        let svg = d3.select(svgRef.current)
            .style('display', 'block')
            .style('background-color', `#${backgroundColor}`)
            .attrs({
                height: dataDomain.length * 30
            })

        let g = d3.select(gRef.current)
        // svg.exit().remove()

        console.log(fields)

        g.selectAll('rect')
            .data(Object.keys(fields), d => d)
            .enter().append('rect').merge(g)
            .attrs({
                x: 20,
                y: (d, i) => 25 * i,
                height: 20,
                width: d => xScale(fields[d]),
                fill: 'blue'
            })

        g.selectAll('text')
            .data(Object.keys(fields), d => d)
            .enter().append('text')
            .text(d => d)
            .attrs({
                x: 0,
                y: (d, i) => (25 * i) + 17,
                height: 20,
                fill: `#${txtColor}`,
            })
        updateData(g)

        // svg.transition()
        //     .duration(300)
        //     .ease(d3.easeBackInOut)
        //     .attr('height', props.height)

        // let section = d3.select(recRef.current);
        // section.transition()
        //     .duration(300)
        //     .ease(d3.easeBackInOut)
        //     .attr('height', props.height)
        //     .on("end", () =>
        //         setHeight(props.height)
        //     );
    }, [])

    return(
        <svg ref={ svgRef } width={width}>
            <g ref={ gRef }>
                {/* <rect
                    ref={recRef}
                    x={0} 
                    y={0} 
                    height={ height } 
                    width={ props.width } 
                    fill={'#575757'} /> */}
            </g>
        </svg>
    )
}

export default Graph;
