import React, { useState } from 'react';
import InputRow from './InputRow';
import { withRouter } from "react-router-dom";
import styled from '@emotion/styled';

const StyledDiv = styled.div`
    display: flex;
    flex-direction: column;
`;

const SubmitButton = styled.button`
    align-self: flex-end;
`;

const Manual = ({ history }) => {
    const [ fields, setFields ] = useState([
        { 
            label: 'Label',
            value: 'Value'
        }
    ]);

    const clickHandler = () => {
        history.push('/graph')
    }

    const saveFields = (label, value) => {
        console.log(fields)
        console.log('UPDATING LABEL', label)
        console.log('UPDATING VALUE', value)
        setFields( prevState => {
            return {...prevState, label: label}
        })
    }

    return(
        <StyledDiv>
            { fields.map( fieldGroup => {
                return (
                <InputRow
                    key={fieldGroup.label}
                    label={ fieldGroup.label }
                    value={ fieldGroup.value }
                    saveFields={ (label, value) => saveFields(label, value) }/>
                )
            })}
            <SubmitButton
                onClick={ () => clickHandler() }>
                Submit
            </SubmitButton>
        </StyledDiv>
    );
}

export default withRouter(Manual);
