import React, { useEffect } from 'react';
import * as d3 from "d3";
import "d3-selection-multi";

const Graph = ({fields, backgroundColor, barsColor, title, padding, textColor, width}) => {
    const svgRef = React.createRef();
    // const recRef = React.createRef();
    const gRef = React.createRef();

    const dataDomain = Object.keys(fields).map( field => {
        return parseInt(fields[field]);
    })

    const xScale = d3.scaleLinear()
        .domain([ 0, d3.max(dataDomain)])
        .range([ 0, width])



    const updateData = () => {
        let svg = d3.select(svgRef.current)
            .style('display', 'block')
            .style('padding', '2rem')
            .style('background-color', `#${backgroundColor}`)
            .attrs({
                height: dataDomain.length * padding
            })

        const header = svg.selectAll('text')
        header.insert('text')
            .merge(header)
            .text(title)
            .attrs({
                y: padding,
                x: 10,
                width: 20,
                height: 20,
                fill: 'white'
            })

        // const header = svg.selectAll('text')
        //     .data([title])

        // header.enter().append('text').merge(header)
        //     .text(title)

        svg.selectAll('g').remove() // clear all previous gs

        let graphic = svg.append('g') // adds a new g

        const rects = graphic.selectAll('rect')
        rects.data(Object.keys(fields), d => d)
            .enter().append('rect')
            .merge(rects)
            .attrs({
                x: 20,
                y: (d, i) => padding * i,
                height: 20,
                width: d => xScale(fields[d]),
                fill: `#${barsColor}`
            })

        const texts = graphic.selectAll('text')
        texts.data(Object.keys(fields), d => d)
            .enter().append('text')
            .merge(texts)
            .text(d => d)
            .attrs({
                x: 0,
                y: (d, i) => (padding * i) + 17,
                height: 20,
                fill: `#${textColor}`,
            })

        /* These don't seem to do anything */
        // graphic.exit().remove()
        // header.exit().remove()
        // rects.exit().remove()
        // texts.exit().remove()
    }



    useEffect(() => {
        updateData()

        // svg.transition()
        //     .duration(300)
        //     .ease(d3.easeBackInOut)
        //     .attr('height', props.height)

        // let section = d3.select(recRef.current);
        // section.transition()
        //     .duration(300)
        //     .ease(d3.easeBackInOut)
        //     .attr('height', props.height)
        //     .on("end", () =>
        //         setHeight(props.height)
        //     );
    })



    return(
        <svg ref={ svgRef } width={width}>
            {/* <g ref={ gRef }>
            </g> */}
        </svg>
    )
}

export default Graph;
