import React, { useState, useEffect } from 'react'
import { withRouter } from "react-router-dom";
import Rectangle from '../components/Rectangle';
import Graph from '../components/Graph';
import styled from '@emotion/styled';

var winWidth = window.innerWidth;
// var winHeight = window.innerHeight;

const StyledRow = styled.div`
    margin: auto;
    width: 75%;
    display: flex;
    flex-direction: column;
    align-items: space-between;
    padding: 1rem;
`;

const GraphOverview = ({ location }) => {
    // const [ expandedState, setExpandedState ] = useState(false);
    const [ title, setTitle ] = useState();
    const [ barColors, setBarColors ] = useState();
    const [ padding, setPadding ] = useState();
    const [ backgroundColor, setBackgroundColor ] = useState();

    const [ labelState, setLabelState ] = useState('lol');
    const [ valueState, setValueState ] = useState('lil');
    // const [ , set ] = useState();
    // const [ , set ] = useState();
    // const [ , set ] = useState();


    const fields = location.state

    const labelChangeHandler = (e) => {
        console.log(e.target.value)
        setLabelState(e.target.value)
    }

    const valueChangeHandler = (e) => {
        console.log(e.target.value)
        setValueState(e.target.value)
    }

    // useEffect( () => {
    //     setState
    // }, [])

    return (
        <StyledRow>
            <h3>Graph Overview</h3>
            {/* { 
                state ? Object.keys(state).map( field => {
                    return <div key={field}>
                    <p>{field}</p>
                    <p>{state[field].value}</p>
                    </div>
                }) : null
            } */}
            <Graph 
                fields={fields}
                title={title}
                barColors={barColors}
                padding={padding}
                backgroundColor={backgroundColor}/>
            {/* <Rectangle
                show={ true }
                width={ winWidth }
                height={ true ? 200 : 0 } /> */}
            <div>
                <StyledRow>
                    <input type="text" placeholder="Label" value={ labelState }
                        onChange={ labelChangeHandler }/>
                    <input type="text" placeholder="Value" value={ valueState }
                        onChange={ valueChangeHandler }/>
                    <button
                        onClick={ () => console.log('saving') }>Add</button>
                </StyledRow>
            </div>
        </StyledRow>
    )
}

export default withRouter(GraphOverview);
